create database coverDB
       character set utf8mb4;

use coverDB;
-- 井盖表
create table cover(
cover_id int primary key auto_increment COMMENT '主键',
cover_base64 longtext COMMENT '图片数据 base_64编码',
upload_time datetime COMMENT '上传时间',
is_judged boolean DEFAULT 0 COMMENT '是否分类',
longitude double(50,6) NOT NULL COMMENT '经度',
latitude double(50,6) NOT NULL COMMENT '纬度',
cover_category varchar(300) DEFAULT 'unknow' COMMENT '井盖类别'
);


